import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import CheckQuizStatusForUser from "./CheckQuizStatusForUser";
import UserSidebar from "./UserSidebar";



const baseUrl='http://127.0.0.1:8000/api';

function CourseQuizList(){
    useEffect(()=>{
        document.title='Тесттер';
    });
   

    const [quizData, setquizData] = useState([]);
    const userId=localStorage.getItem('userId')
    const {course_id} = useParams();

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch-assigned-quiz/'+ course_id).then((res)=>{
          setquizData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);
    

    return(
        <>
         <div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <UserSidebar/>
            </aside>
            <div className="col-9">
              <div className="card">
                 <h4 id="add-word">Барлық тесттер тізімі</h4>
                 <div className="card-body">

                 <table className="table table-bordered pb-1">
  <tbody>
  {quizData.map((row, index)=> 
            <tr key={index}>
              <td>{index + 1}</td>
              <td><h5 className="card-title">{row.quiz.title}</h5></td>
              <td> <CheckQuizStatusForUser quiz={row.quiz.id} user={userId}/></td>
              </tr>
          )}
</tbody>
</table>

     
        </div>
              </div>
            </div> 
          </div>
        </div>
        </div>  
 
        </>
    );
}
export default CourseQuizList;
