import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const baseUrl='http://127.0.0.1:8000/api';

function StudyMaterials(){

    useEffect(()=>{
        document.title='Study materials';
    });

    const [studyData, setstudyData] = useState([]);
    const [totalResult, settotalResult] = useState(0);
    const {course_id} = useParams();
    const [courseData, setCourseData] = useState([]); 
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/study-materials/'+ course_id).then((res)=>{
                setstudyData(res.data);
                settotalResult(res.data.length);
            });
          }catch(error){
            console.log(error);
          }

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const handleDeleteClick = (study_id) => {
        Swal.fire({
            title: 'Confirm',
            text: "Are you sure you want to delete this data?",
            icon: 'info',
            confirmButtonText: 'Continue',
            showCancelButton: true
          }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/study-material/'+ study_id).then((res)=>{
                        Swal.fire('success', 'Data has been deleted');
                        try{
                            axios.get(baseUrl + '/study-materials/'+ course_id).then((res)=>{
                                settotalResult(res.data.length)
                                setstudyData(res.data);
                            });
                          }catch(error){
                            console.log(error);
                          }
                    });
                  }catch(error){
                    Swal.fire('error', 'Data has not been deleted');
                  }
            }else{
                Swal.fire('error', 'Data has not been deleted');
            }
          });
    }
    return (
        <>

<div id="background">
      <TeacherHeader />
      <Link className="btn btn-warning" to={"/add-study-material/"+course_id}>Add Study Material</Link>
      <div className="container mt-5" >
        <div id="my"><h4>All study materials {courseData.title}</h4></div>    
        <div className="row mb-4">
        {studyData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title">{row.title}</h5>
             
               <Link to={row.upload}>Files</Link>

               <h5>{row.remarks}</h5>
               <button  onClick={() => handleDeleteClick(row.id)} className="btn btn-danger"><i className="bi bi-trash3-fill"></i></button>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
    </div> 


        </>
    );
}
export default StudyMaterials;
