import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function CheckQuizStatusForUser(props){

    useEffect(()=>{
        document.title='Assign Quiz';
    });

    const [quizData, setquizData] = useState([]);
    const[assignStatus, setassignStatus]=useState();
    const [courseData, setCourseData] = useState([]); 
    const userId=localStorage.getItem('userId');
    const {course_id}=useParams();
    

    useEffect(()=>{
        try{
            axios.get(`${baseUrl}/fetch-quiz-attempt-status/${props.quiz}/${props.user}`).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }

    }, []);


    return(
        <>

<div>
    
    {quizData.bool === true &&
    <Link to={`take-quiz/${props.quiz}`} className="btn btn-success">Take Quiz</Link>
   }
   {quizData.bool === false &&
    <span className="btn btn-success">Assigned</span>
   }
     
</div>   
 


      
        </>
    );
}
export default CheckQuizStatusForUser;
