import { useState, useEffect  } from "react";
import axios from "axios";
import TeacherSidebar from "./TeacherSidebar";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherAddCourse() {

    useEffect(()=>{
        document.title='Курс қосу';
      });

    const [cats, setCats]=useState([]);
    const [courseData, setCourseData] = useState({
        category: '',
        title: '',
        description: '',
        technologies: ''
      });

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/category').then((res)=>{
                setCats(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);
    
    const handleChange=(event)=>{
        setCourseData({
          ...courseData,
          [event.target.name]: event.target.value
        });
      }

   

      const submitForm=()=>{
        const teacherId=localStorage.getItem('teacherId');
        const _formData = new FormData();
        _formData.append("category", courseData.category)
        _formData.append("teacher", teacherId)
        _formData.append("title", courseData.title)
        _formData.append("description", courseData.description)
        _formData.append("technologies", courseData.technologies)
    
        try{
          axios.post(baseUrl+'/course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200 || res.status===201){
              Swal.fire({
                title: 'Курс сәтті қосылды!',
                icon: 'success',
                iconColor: 'lightpink',
                toast: true,
                timer: 5000,
                position: "top-right",
                timerProgressBar: true,
                showConfirmButton: false
              });
              window.location.href='/teacher-add-course';
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
      <div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <TeacherSidebar/>
            </aside>
            <div className="col-9">
              <div className="card">
                 <h4 id="add-word">Курс қосу</h4>
                 <div className="card-body">
                 <form>
           <div className="form-group">
             <select name="category" defaultValue={'DEFAULT'} onChange={handleChange} className="form-control" id="InputCategory" >
              <option>Курстың категориясын таңдаңыз</option>
              {cats.map((category, index)=>{return <option key={index} value={category.id}>{category.title}</option>})}
             </select>
           </div>

           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Курстың атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description" onChange={handleChange} className="form-control" id="InputDescription" placeholder="Курстың сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{marginTop: '40px'}}>
             <textarea name="technologies" onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Технологиялар"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
                 </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
        </>
    );
}
export default TeacherAddCourse;
