import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


const baseUrl='http://127.0.0.1:8000/api';

function UserMyCourses(){
    useEffect(()=>{
        document.title='User My Courses';
    });

    const [courseData, setCourseData] = useState([]);

    const studentId=localStorage.getItem('studentId');

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch_enrolled_courses/'+ studentId).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);
    

    return(
        <>
        <UserHeader />
        User My Courses
        <div className="container mt-4" >
        <div id="my"><h4>Менің курстарым</h4></div>    
        <div className="row mb-4">
         {courseData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={row.course.featured_img} className="card-img-top" alt="..."></img>
            <div className="card-body">
             <h5 className="card-title">{row.course.title}</h5>
             <p>{row.course.teacher}</p>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 
        </>
    );
}
export default UserMyCourses;
