import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const baseUrl='http://127.0.0.1:8000/api';

function UserMyCourses(){
    useEffect(()=>{
        document.title='User My Courses';
    });
   

    const [courseData, setCourseData] = useState([]);

    const userId=localStorage.getItem('userId');
    const [searchString, setSearchString] = useState({
      'search': ''
    });

    //Change Element Value
const handleChange=(event)=>{
  console.log(searchString)
  setSearchString({
    ...searchString,
    [event.target.name]: event.target.value
    
  });
}


   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch_enrolled_courses/'+ userId).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);

const unenroll = (user_id) => {
  
}

    
    const searchCourse = () =>{
      window.location.href='/user-search/'+ searchString.search
    }
    
    return(
        <>
        <UserHeader />
        <div id="my"><h4>Менің курстарым</h4></div> 

        <nav className="navbar navbar-light bg-light">
         <div className="container-fluid">
          <form className="d-flex">
           <input name="search" onChange={handleChange} className="form-control me-2" type="search" placeholder="Search by title" aria-label="Search"/>
           <button onClick={searchCourse} className="btn btn-outline-success" type="button">Search</button>
          </form>
         </div>
        </nav>

        <div className="container mt-4" >   
        <div className="row mb-4">
         {courseData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
           
            <div className="card-body">
             <Link to={'/course_detail/'+ row.course.id}><h5 className="card-title">{row.course.title}</h5></Link>
             <p>{row.course.teacher.full_name}</p>
             <Link to={'/user-quiz-list/'+ row.course.id} className="btn btn-warning">Quiz List</Link>
             <button onClick={()=>unenroll(userId)}>Unenroll</button>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 
        </>
    );
}
export default UserMyCourses;
