import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import UserHeader from "./User/UserHeader";

const baseUrl='http://127.0.0.1:8000/api';

function AvailableCourses(){

    useEffect(()=>{
        document.title='Available Courses';
    });

    const [courseData, setCourseData] = useState([]);
    const [searchString, setSearchString] = useState({
      'search': ''
    });

    useEffect(()=>{
      try{
          axios.get(baseUrl + '/course').then((res)=>{
              setCourseData(res.data);
          });
        }catch(error){
          console.log(error);
        }
  }, []);

      //Change Element Value
const handleChange=(event)=>{
  console.log(searchString)
  setSearchString({
    ...searchString,
    [event.target.name]: event.target.value
    
  });
}

const searchCourse = () =>{
  window.location.href='/general-search/'+ searchString.search
}

    return(
      <>
      <UserHeader/>

      <nav className="navbar navbar-light bg-light">
         <div className="container-fluid">
          <form className="d-flex">
           <input name="search" onChange={handleChange} className="form-control me-2" type="search" placeholder="Search by title" aria-label="Search"/>
           <button onClick={searchCourse} className="btn btn-outline-success" type="button">Search</button>
          </form>
         </div>
        </nav>

     <div id="background1">
      <div className="container mt-4" >
        <div id="my"><h4>Курстар</h4></div>    
        <div className="row mb-4">
            {Array.isArray(courseData) && courseData.map((course, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" alt="..."></img>
            <div className="card-body">
            <Link to={'/course_detail/'+ course.id}><h5 className="card-title">{course.title}</h5></Link>
             <p className="card-text">Үйренушілер саны: {course.total_enrolled_students}</p>
             
            </div>
           </div>
         </div>
           )}
        </div>
     </div>  
 


     </div> 
      </>
    );
}
export default AvailableCourses
