import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";


const baseUrl='http://127.0.0.1:8000/api';

function AllQuestions(){

    useEffect(()=>{
        document.title='All Questions';
    });

    const [questionData, setquestionData] = useState([]);
    const [quizData, setquizData] = useState([])
    const [totalResult, settotalResult] = useState(0);
    const {quiz_id} = useParams();
    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/quiz-questions/'+ quiz_id).then((res)=>{
                setquestionData(res.data);
                settotalResult(res.data.length);
            });
          }catch(error){
            console.log(error);
          }

          try{
            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const handleDeleteClick = (question_id) => {
        Swal.fire({
            title: 'Confirm',
            text: "Are you sure you want to delete this question?",
            icon: 'info',
            confirmButtonText: 'Continue',
            showCancelButton: true
          }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/question/'+ question_id).then((res)=>{
                        Swal.fire('success', 'Data has been deleted');
                        try{
                            axios.get(baseUrl + '/quiz-questions/'+ quiz_id).then((res)=>{
                                settotalResult(res.data.length)
                                setquestionData(res.data);
                            });
                          }catch(error){
                            console.log(error);
                          }
                    });
                  }catch(error){
                    Swal.fire('error', 'Data has not been deleted');
                  }
            }else{
                Swal.fire('error', 'Data has not been deleted');
            }
          });
    }
    
    

    return (
        <>
        <h1>{quizData.title}</h1>

<table className="table">
  <thead>
    <tr>
      <th scope="col">Question</th>
      <th scope="col">Action</th>
    </tr>
  </thead>
  <tbody>
  {questionData.map((row, index) => (
            <tr>
              <td>{row.question}</td>
              <td><button onClick={()=> handleDeleteClick(row.id)}>Delete</button></td>
            </tr>
          ))}
</tbody>
</table>
 
       <Link to={"/teacher-add-question/" + quiz_id}>Add question</Link>

        </>
    );
}
export default AllQuestions;
