import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';

function EnrolledStudents() {

    useEffect(()=>{
        document.title='My Courses';
    });

    const [studentData, setStudentData] = useState([]);

    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-courses/'+ teacherId).then((res)=>{
                setStudentData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    return (
        <>
      <div id="background">
      <TeacherHeader />
    
      <div className="container mt-4" >
        <div id="my"><h4>Студенттер</h4></div>    
        <div className="row mb-4">
         {studentData.map((course, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" alt="..."></img>
            <div className="card-body">
             <h5 className="card-title"><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h5>
             <p className="card-text">Үйренушілер саны: {course.total_enrolled_students}</p>
             <button>View</button>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 


     </div>  
        </>
    );
}
export default EnrolledStudents;
