import { useState, useEffect  } from "react";
import axios from "axios";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function UserForgotPassword() {
    useEffect(()=>{
        document.title='Ұмытылған құпиясөз';
      });

    const [userData, setuserData] = useState({
      'email': ''
    });
    const [successMsg, setsuccessMsg] = useState([]);
    const [errorMsg, seterrorMsg] = useState([]);

  const handleChange=(event)=>{
    setuserData({
      ...userData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const userFormData = new FormData();
    userFormData.append("email", userData.email)

    try{
      axios.post(baseUrl + '/user-forgot-password/', userFormData).then((response)=>{
        if(response.data.bool===true){
            setsuccessMsg(response.data.msg);
            seterrorMsg('');
        }else{
            seterrorMsg(response.data.msg);
            setsuccessMsg('');
        }
      });
    }catch(error){
        console.log(error);
        setuserData({'status': 'error'});
    }  

  };

  const userLoginStatus=localStorage.getItem('userLoginStatus')
  if(userLoginStatus=='true'){
    window.location.href='/user-profile';
  }



    return (
        <>
         {/* Left Part */}
       <div className="leftSide"></div>
        
        {/* Right Part */}
       <div className="rightSide" id="forgot">
       <div id = "register-info">
        
         <h3 id='header-forgot'>Тіркелген поштаңызды жазыңыз</h3>
        {successMsg && <p className='text-success'>{successMsg}</p>}
        {errorMsg && <p className='text-danger'>{errorMsg}</p>}
        <div className="form-group">
              <input name="email" type="email" value={userData.email} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="Пошта"></input>
        </div>

        <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Жүктеу</button>
        </div>
        </div>
      </div>
        </>
    );
}
export default UserForgotPassword;
