import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyCourses() {

    useEffect(()=>{
        document.title='My Courses';
    });

    const [courseData, setCourseData] = useState([]);

    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-courses/'+ teacherId).then((res)=>{
                setCourseData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    return (
        <>
        
        <TeacherHeader />
         
         <div style={{width: '300px'}}>
            {courseData.map((course, index) => 
            <div style={{padding: '20px', backgroundColor: 'white'}}>
               <h1><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h1>
               <div><img src={course.featured_img} alt="SS" width="100px" height="100px"></img></div>
               <h5>Total enrolled: 123</h5>
               <button>Delete</button>
               <Link to={'/teacher-add-chapter/'+ course.id}><button >Add Chapter</button></Link>
               <Link to={'/teacher-edit-course/'+ course.id}><button >Edit</button></Link>
            </div>
            )}
         </div>

         <div class="card-group" style={{width: '300px'}}>
          <div class="card">
          {courseData.map((course, index) => 
           <div class="card-body">
             <h5 class="card-title"><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h5>
             <div><img src={course.featured_img} alt="SS" width="100px" height="100px"></img></div>
             <p class="card-text">Total enrolled: 123</p>
             <button>Delete</button>
             <Link to={'/teacher-add-chapter/'+ course.id}><button >Add Chapter</button></Link>
             <Link to={'/teacher-edit-course/'+ course.id}><button >Edit</button></Link>
           </div>
           )}
          </div>
         </div>
        </>
    );
}
export default TeacherMyCourses;
