import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';


function TeacherProfile() {
  useEffect(()=>{
    document.title='Profile';
});

  const teacherId = localStorage.getItem("teacherId")
  const [teacherData, setTeacherData] = useState(null)

  useEffect(() => {
    try {
        axios.get(baseUrl + '/teacher/' + teacherId + '/').then((res) => {
            setTeacherData(res.data)
        })
    } catch (error) {
        console.log(error)
    }
}, [])

if (!teacherData) {
    return <div>Loading...</div>
}


    return (
        <>
        <div id="whole-page">
          <TeacherHeader />

        <h1>Teacher Profile</h1>
        <img src={teacherData.profile_img} className="card-img-top" alt="..."></img>
        <div>{teacherData.full_name}</div> 
        <div>{teacherData.email}</div>
        <div>{teacherData.qualification}</div>
        <div>{teacherData.mobile_no}</div>   
        <div>{teacherData.skills}</div>  
        </div>
        </>
    );
}
export default TeacherProfile;
