import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import UserSidebar from "./User/UserSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function GeneralSearch(){
    useEffect(()=>{
        document.title='Іздеу...';
    });
   

    const [courseData, setCourseData] = useState([]);
    const {searchstring} = useParams();

   

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/general-search-courses/'+ searchstring).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);


    return(
        <>
         <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Курс қосу</h4>
                 <div class="card-body">

        <div className="row mb-4">
            {Array.isArray(courseData) && courseData.map((course, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" alt="..."></img>
            <div className="card-body">
            <Link to={'/course_detail/'+ course.id}><h5 className="card-title">{course.title}</h5></Link>
             <p className="card-text">Үйренушілер саны: {course.total_enrolled_students}</p>
             
            </div>
           </div>
         </div>
           )}
        </div>
     </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
       
 
        </>
    );
}
export default GeneralSearch;
