import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyQuizzes(){

    useEffect(()=>{
        document.title='My Quizzes';
    });

    const [quizData, setquizData] = useState([]);
    const [totalRes, settotalRes] = useState(0);
    
    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    const handleDelete = (quiz_id) => {
        Swal.fire({
            title: 'Confirm',
            text: "Are you sure you want to delete this quiz?",
            icon: 'info',
            confirmButtonText: 'Continue',
            showCancelButton: true
          }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/quiz/'+ quiz_id).then((res)=>{
                        Swal.fire('success', 'Data has been deleted');
                        try{
                            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                                settotalRes(res.data.length)
                                setquizData(res.data);
                            });
                          }catch(error){
                            console.log(error);
                          }
                    });
                  }catch(error){
                    Swal.fire('error', 'Data has not been deleted');
                  }
            }else{
                Swal.fire('error', 'Data has not been deleted');
            }
          });
    }
    

    return(
        <>
        <TeacherHeader/>
        <Link to="/teacher-add-quiz">Add Quiz</Link>

        <div className="container mt-4" >
        <div id="my"><h4>Менің quizzes</h4></div>    
        <div className="row mb-4">
         {quizData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title"><Link to={'#'}>{row.title}</Link></h5>
             <p>Enrolled: <Link to={'#'} className="card-text">Total 123</Link></p>
             <Link to={'/teacher-edit-quiz/' + row.id} className="card-text">Edit quiz</Link>
             <button onClick={()=>handleDelete(row.id)}>Delete</button>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 


      
        </>
    );
}
export default TeacherMyQuizzes;
