import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function AssignQuiz(){

    useEffect(()=>{
        document.title='Assign Quiz';
    });

    const [quizData, setquizData] = useState([]);
    const[assignStatus, setassignStatus]=useState();
    const [courseData, setCourseData] = useState([]); 
    const teacherId=localStorage.getItem('teacherId');
    const {course_id}=useParams();
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-quizzes/'+ teacherId).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }


    }, []);


    const assignQuiz=(quiz_id) => {
        const _formData = new FormData();
        _formData.append("course", course_id)
        _formData.append("quiz", quiz_id)
    
        try{
          axios.post(baseUrl+'/quiz-assign-course/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status===200||res.status===201){
                Swal.fire({
                    title: 'Assigned to this course!',
                    icon: 'success',
                    toast: true,
                    timer: 10000,
                    position: "top-right",
                    timerProgressBar: true,
                    showConfirmButton: false
                });
            }
          });
        }catch(error){
            console.log(error);
            
        }
    } 

    return(
        <>

        <div className="container mt-4" >
        <div id="my"><h4>Assign Quiz ({courseData.title})</h4></div>    
        <div className="row mb-4">
         {quizData.map((row, index) => 
        
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title"><Link to={'/teacher-all-questions/'+ row.id}>{row.title}</Link></h5>
             {row.assign_status == 0 &&
             <button onClick={()=>assignQuiz(row.id)}>Assign Quiz</button>
            }
            </div>
           </div>
         </div>
        
         )}
        </div>
     </div>  
 


      
        </>
    );
}
export default AssignQuiz;
