import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function AddQuestion() {

    useEffect(()=>{
        document.title='Add Question';
      });

    const [questionData, setquestionData] = useState({
        quiz: '',
        question: '',
        ans1: '',
        ans2: '',
        ans3: '',
        ans4: '',
        right_ans: ''
      });
    
    const handleChange=(event)=>{
        setquestionData({
          ...questionData,
          [event.target.name]: event.target.value
        });
      }

      const {quiz_id}=useParams();

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('quiz', quiz_id)
        _formData.append('question', questionData.question)
        _formData.append('ans1', questionData.ans1)
        _formData.append('ans2', questionData.ans2)
        _formData.append('ans3', questionData.ans3)
        _formData.append('ans4', questionData.ans4)
        _formData.append('right_ans', questionData.right_ans)
    
        try{
          axios.post(baseUrl+'/quiz-questions/'+quiz_id, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status == 200 || res.status == 200){
                Swal.fire({
                  title: 'added',
                  icon: 'success',
                  toast: true,
                  timer: 3000,
                  position: 'top-right',
                  timerProgressBar: true,
                  showConfirmButton: false
                });
                window.location.reload();
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
      <div id="background1">
       <TeacherHeader />
        <div class="container mt-4" >
         <div id="my"><h4>Quiz question қосу</h4></div>
         <form>
           
           <div className="form-group">
             <input name="question" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Сұрақ:"></input>
           </div>

           <div className="form-group">
             <input name="ans1" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="А:"></input>
           </div>

           <div className="form-group">
             <input name="ans2" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Ә:"></input>
           </div>

           <div className="form-group">
             <input name="ans3" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Б:"></input>
           </div>

           <div className="form-group">
             <input name="ans4" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="В:"></input>
           </div>

           <div className="form-group">
             <input name="right_ans" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Дұрыс жауап"></input>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
        </div>
      </div>
        </>
    );
}
export default AddQuestion;
