import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


const baseUrl='http://127.0.0.1:8000/api';

function UserMyCourses(){
    useEffect(()=>{
        document.title='User My Courses';
    });
   

    const [courseData, setCourseData] = useState([]);

    const userId=localStorage.getItem('userId');
    console.log(userId)

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch_enrolled_courses/'+ userId).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);
    

    return(
        <>
        <UserHeader />
        
        <div className="container mt-4" >
        <div id="my"><h4>Менің курстарым</h4></div>    
        <div className="row mb-4">
         {courseData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
           
            <div className="card-body">
             <Link to={'/course_detail/'+ row.course.id}><h5 className="card-title">{row.course.title}</h5></Link>
             <p>{row.course.teacher.full_name}</p>
             <Link to={'/course_quiz/'+ row.course.id} className="btn btn-warning">Quiz List</Link>
             <button>Unenroll</button>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  
 
        </>
    );
}
export default UserMyCourses;
