import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyCourses() {

    useEffect(()=>{
        document.title='My Courses';
    });

    const [courseData, setCourseData] = useState([]);

    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-courses/'+ teacherId).then((res)=>{
                setCourseData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const [searchString, setSearchString] = useState({
      'search': ''
    });

    //Change Element Value
const handleChange=(event)=>{
  console.log(searchString)
  setSearchString({
    ...searchString,
    [event.target.name]: event.target.value
    
  });
}

    const searchCourse = () =>{
      window.location.href='/teacher-search/'+ searchString.search
    }


    return (
        <>
      

      <nav className="navbar navbar-light bg-light">
         <div className="container-fluid">
          <form className="d-flex">
           <input name="search" onChange={handleChange} className="form-control me-2" type="search" placeholder="Search by title" aria-label="Search"/>
           <button onClick={searchCourse} className="btn btn-outline-success" type="button">Search</button>
          </form>
         </div>
        </nav>
    
        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Менің курстарым</h4>
                 <div class="card-body">    
        <div className="row mb-4 pl-5">
         {courseData.map((course, index) => 
          <div className="col-md-5 mb-3">
           <div className="card" id="my-courses-cards">
            <img src={course.featured_img} className="card-img-top" height='170' alt="..."></img>
            <div className="card-body">
             <h5 className="card-title"><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h5>
             <p>Enrolled: <Link to={'/teacher-enrolled-students/'+ course.id} className="card-text">{course.total_enrolled_students}</Link></p>
            </div>
           </div>
         </div>
         )}
        </div>
        
        </div>
              </div>
            </div> 
          </div>
        </div>
        </div>   
        </>
    );
}
export default TeacherMyCourses;
