import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


const baseUrl='http://127.0.0.1:8000/api';

function AllChapters(){

    useEffect(()=>{
        document.title='All Chapters';
    });

    const [chapterData, setChapterData] = useState([]);
    const [totalResult, settotalResult] = useState(0);
    const {course_id} = useParams();
    const [assignmentData, setassignmentData] = useState([])
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/course-chapters/'+ course_id).then((res)=>{
                setChapterData(res.data);
                settotalResult(res.data.length);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const Swal = require('sweetalert2');
    const handleDeleteClick=()=>{
        Swal.fire({
            title: 'Растау',
            text: 'Сабақты өшіргіңіз келе ме?',
            icon: 'info',
            confirmButtonText: 'Cool',
            showCancelButton: true
          })
    }

    useEffect(()=>{
      try{
          axios.get(baseUrl + '/assignment/'+ course_id).then((res)=>{
              setassignmentData(res.data);
              //settotalResult(res.data.length);
          });
        }catch(error){
          console.log(error);
        }
  }, []);

    return (
        <>

<div id="background">
      <TeacherHeader />

      <Link className="btn btn-info" to={"/assign-quiz/"+course_id}>Assign quiz</Link>
      <Link className="btn btn-warning" to={"/teacher-study-materials/"+course_id}>Study Materials</Link>
      <Link className="btn btn-primary" to={"/teacher-add-chapter/"+course_id}>AddChapter</Link>
      <Link className="btn btn-success" to={"/teacher-edit-course/"+course_id}>Edit Course</Link>
      <Link to={"/teacher-add-assignment/"+course_id} className="btn btn-dark">Add Assignment</Link>

      <div className="container mt-5" >
        <div id="my"><h4>Барлық сабақтар ({totalResult})</h4></div>    
        <div className="row mb-4">
        {chapterData.map((chapter, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title"><Link to={'/edit-chapter/'+chapter.id}>{chapter.title}</Link></h5>
             
               {/* Should be content or video here */}

               <h5>{chapter.remarks}</h5>
               <button  onClick={handleDeleteClick} className="btn btn-danger"><i className="bi bi-trash3-fill"></i></button>
               <Link to={'/teacher-edit-chapter/'+chapter.id}  className="btn btn-success"><i className="bi bi-pencil-square"></i></Link>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>

     <div class="container mt-5" >
        <div id="my"><h4>All assignments</h4></div>    
        <div class="row mb-4">
        {assignmentData.map((row, index) => 
          <div class="col-md-4 mb-3">
           <div class="card" id="my-courses-cards">
            <div class="card-body">
             <h5 class="card-title">{row.title}</h5>
             
               <Link to={row.upload}>Files</Link>

               <h5>{row.detail}</h5>
               <button className="btn btn-danger"><i className="bi bi-trash3-fill"></i></button>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  

     </div> 
        </>
    );
}
export default AllChapters;
