import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function TeacherSearch(){
    const [courseData, setCourseData] = useState([]);
    const {searchstring} = useParams();

   

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/teacher-search-courses/'+ searchstring).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);


    return(
        <>

<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Менің курстарым</h4>
                 <div class="card-body" id="course-body">

                 <div className="row mb-4">
                 {courseData.map((course, index) => 
          <div className="col-md-5 mb-3">
           <div className="card" id="my-courses-cards">
           <img src="https://images.unsplash.com/photo-1503455637927-730bce8583c0?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80" className="card-img-top" height='100'  alt="..."></img>
            <div className="card-body">
             <Link id="a" to={'/course_detail/'+ course.id}><h5 className="card-title">{course.title}</h5></Link>
             <p>{course.teacher.full_name}</p>
             <Link id="unenroll" to={'/user-quiz-list/'+ course.id} style={{color: 'rgb(191, 57, 79)'}}>Тесттер тізімі</Link><br></br>
            </div>
           </div>
         </div>
         )}
       </div>

        </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
 
 
 
        </>
    );
}
export default TeacherSearch;
