import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import CheckQuizStatusForUser from "./CheckQuizStatusForUser";



const baseUrl='http://127.0.0.1:8000/api';

function CourseQuizList(){
    useEffect(()=>{
        document.title='User My Quizzes';
    });
   

    const [quizData, setquizData] = useState([]);
    const userId=localStorage.getItem('userId')
    const {course_id} = useParams();

   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch-assigned-quiz/'+ course_id).then((res)=>{
          setquizData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);
    

    return(
        <>
        <UserHeader />
        
        <div className="container mt-4" >
        <div id="my"><h4>Quiz List</h4></div>    
        <div className="row mb-4">
        {quizData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
           
            <div className="card-body">
             <h5 className="card-title">{row.quiz.title}</h5>
             <CheckQuizStatusForUser quiz={row.id} user={userId}/>
            </div>
           </div>
         </div>
        )}
        </div>
     </div>  
 
        </>
    );
}
export default CourseQuizList;
