import './Home.css';
import CommonLogin from './CommonLogin';
import CommonSignUp from './CommonSignUp';
import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';

function Home() {
  useEffect(()=>{
    document.title='Home Page';
  });

  const [modalOpen, setModalOpen] = useState(false);
  const [modalSignupOpen, setSignupModalOpen] = useState(false);

  return (
    <>
     
      <div>
        <div id="header-element1">
         <Link to="/"><p className="logo" >lulu school</p></Link>
        </div>

        <div id ="header-element2">
          <ul>
           <li className="nav-item">
            <button id="login-button" onClick={() => {setModalOpen(true); }}>КІРУ</button>
           </li>
           <li className="nav-item">
            <button id="login-button">ҚАЗ</button>
           </li>
          </ul>
        </div>

      </div>


      <div id="body-element">
        <h1>ҚЫТАЙ ТІЛІН БІЗБЕН ҮЙРЕНІҢІЗ!</h1>
        <div id="text">
          <p>Қытай тілінің онлайн оқытушысын  іздеп жүрсіз бе? Қытай тілі – бүкіл әлемдегі тілдерді үйренуге арналған жетекші онлайн платформа.</p>
        </div>
        <button id="login-button" onClick={() => {setSignupModalOpen(true); }}>Бастау</button>

        {modalOpen && <CommonLogin setOpenModal={setModalOpen} />}
        {modalOpen && <CommonSignUp setSignupOpenModal={setSignupModalOpen} />}
      </div>
    </>
  );
}

export default Home;
