import { useState, useEffect  } from "react";
import axios from "axios";
import {Link, useParams } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherDetail(){
    let {teacher_id}=useParams();
    const [teacherData, setteacherData] = useState([])

  useEffect(()=>{

      try{
        axios.get(baseUrl + '/teacher/' + teacher_id).then((res)=>{
            setteacherData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

}, []);


    return(
        <>

<div id="back-img">
    <div className="card" id="user-det">
        <div class="container mt-3" >
          <div class="row">
            <div class="col-4">
            <div><img src={teacherData.profile_img} id="det-img" className="card-img-top" alt="..."></img></div>
            </div>
            <div class="col-6" id="user-info">
                <h4 id="det-word">{teacherData.full_name}</h4>
                <h6><b>Пошта:</b> {teacherData.email}</h6>
                <h6><b>Біліктілігі:</b> {teacherData.qualification}</h6>
                <h6><b>Тел. нөмірі:</b> {teacherData.mobile_no}</h6>
                <h6><b>Қабілеттілігі:</b> {teacherData.skills}</h6>

        {teacherData.facebook_url &&
         <p id="social-det"><Link to={teacherData.facebook_url}><i className="bi bi-facebook"></i></Link></p>
        }
        {teacherData.twitter_url &&
         <p id="social-det"><Link to={teacherData.twitter_url}><i className="bi bi-twitter"></i></Link></p>
        } 
        {teacherData.instagram_url &&
         <p id="social-det"><Link to={teacherData.instagram_url}><i className="bi bi-instagram"></i></Link></p>
        }
        {teacherData.website_url &&
         <p id="social-det"><Link to={teacherData.website_url}><i className="bi bi-globe2"></i></Link></p>
        } 
            </div> 
          </div>
        </div>
        </div>
        </div>
          

        </>
    );
}
export default TeacherDetail;
