import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function EditChapter() {

  const { chapter_id } = useParams();
  const [chapterData, setChapterData] = useState({
    course: '',
    title: '',
    description: '',
    upload: '',
    remarks: ''
  });
  const courseId = localStorage.getItem('courseId');

  useEffect(() => {
    document.title = 'Edit Chapter';
    try {
      axios.get(baseUrl + '/chapter/' + chapter_id + '/').then((res) => {
        setChapterData(res.data);
      });
    } catch (error) {
      console.log(error);
    }
  }, [chapter_id]);

  const handleChange = (event) => {
    setChapterData({
      ...chapterData,
      [event.target.name]: event.target.value
    });
  }

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setChapterData({
      ...chapterData,
      [event.target.name]: file
    });
  }

  const submitForm = async () => {
    const formData = new FormData();
    formData.append('course', courseId)
    formData.append('title', chapterData.title)
    formData.append('description', chapterData.description)
    formData.append('upload', chapterData.upload)
    formData.append('remarks', chapterData.remarks)

    try {
      const res = await axios.put(baseUrl + '/chapter/' + chapter_id + '/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      console.log(res);
      if (res.status === 200 || res.status === 201) {
        Swal.fire({
          title: 'Data updated',
          icon: "success",
          timer: 3000,
          timerProgressBar: true,
          showConfirmButton: false
        });
        // Redirect to the page after successful update
        // window.location.href = '/teacher-add-chapter/5';
      }
    } catch (error) {
      console.log(error);
      // Show error message to the user
      Swal.fire({
        title: 'Error',
        text: error.response?.data?.detail || 'Something went wrong',
        icon: 'error',
        timer: 3000,
        timerProgressBar: true,
        showConfirmButton: false
      });
    }
  };


    return (
        <>
        <div id="background1">
        <TeacherHeader />
        <div className="container mt-4" >
        <div id="my"><h4>Chapter өзгеру</h4></div>
        <form>
        <div className="form-group">
             <input name="title" value={chapterData.title} onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Бөлімнің атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description" value={chapterData.description} onChange={handleChange} className="form-control" id="InputDescription" placeholder="Бөлімнің сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>

           <div className="form-group">
             <textarea name="remarks" value={chapterData.remarks} onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Ескертулер"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
         </div>
         </div>
        </>
    );
}
export default EditChapter;
