import './TeacherLogin.css';
import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';
import axios from 'axios';

const baseUrl='http://127.0.0.1:8000/api';
function TeacherLogin() {

  useEffect(()=>{
    document.title='Teacher Log In';
  });

  const [teacherLoginData, setteacherLoginData] = useState({
    email: '',
    password: ''
  });
  const [errorMSg, seterrorMsg] = useState([]);

  const handleChange=(event)=>{
    setteacherLoginData({
      ...teacherLoginData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const teacherFormData = new FormData();
    teacherFormData.append('email', teacherLoginData.email)
    teacherFormData.append('password', teacherLoginData.password)

    try{
      axios.post(baseUrl + '/teacher-login', teacherFormData).then((res)=>{
        if(res.data.bool===true){
          //localStorage.setItem('teacherLoginStatus', true);
          //localStorage.setItem('teacherId', res.data.teacher_id);
          window.location.href='/teacher-verify'+res.data.id;
        }else{
          seterrorMsg(res.data.msg);
        }
      });
    }catch(error){
      console.log(error);
    }


  };

  const teacherLoginStatus=localStorage.getItem('teacherLoginStatus')
  if(teacherLoginStatus==='true'){
    window.location.href='/teacher-profile';
  }

  return (
    <>
       {/* Left Part */}
       <div className="leftPart">
         <Link to="/"><p className="logo" >lulu school</p></Link>
         <div id = "register-info">
          <h3 id="header-w">Аккаунтқа кіру</h3>
         
             {errorMSg && <p className='text-danger'>{errorMSg}</p>}
            <div className="form-group">
             <input name="email" value={teacherLoginData.email} onChange={handleChange} type="email" className="form-control" id="InputEmail1" aria-describedby="emailHelp" placeholder="Электрондық пошта"></input>
           </div>

            <div className="form-group">
              <input name="password" type="password" value={teacherLoginData.password} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="Құпия сөз"></input>
            </div>

            <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">Кіру</button>
            </div>

            <div className="form-group" id="word-in-bottom">
               <p>Аккаунтыңыз жоқ па? <Link to="/teacher-signup" id="a">Тіркелу</Link></p>
            </div>

         
         </div>
       </div>
       
       
       {/* Right Part */}
       <div className="rightPart"></div>
    </>
  );
}

export default TeacherLogin;
