import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function AddStudyMaterial() {

    useEffect(()=>{
        document.title='Оқу материалын қосу';
      });

    const [studyData, setstudyData] = useState({
        title: '',
        description: '',
        upload: '',
        remarks: ''
      });
    
    const handleChange=(event)=>{
        setstudyData({
          ...studyData,
          [event.target.name]: event.target.value
        });
      }

    const handleFileChange=(event)=>{
        window.URL=window.URL || window.webkitURL;
        var upload = document.createElement('upload');
        upload.src = URL.createObjectURL(event.target.files[0])

        setstudyData({
          ...studyData,
          [event.target.name]: event.target.files[0]
        });
      }

      const {course_id}=useParams();

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('course', course_id)
        _formData.append('title', studyData.title)
        _formData.append('description', studyData.description)
        _formData.append('upload', studyData.upload, studyData.upload.name)
        _formData.append('remarks', studyData.remarks)
    
        try{
          axios.post(baseUrl+'/study-materials/'+course_id, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status == 200 || res.status == 201){
                Swal.fire({
                  title: 'Оқу матералы сәтті қосылды!',
                  icon: 'success',
                  iconColor: 'lightpink',
                  toast: true,
                  timer: 5000,
                  position: "top-right",
                  timerProgressBar: true,
                  showConfirmButton: false
                });
                window.location.reload();
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
      <div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <TeacherSidebar/>
            </aside>
            <div className="col-9">
              <div className="card">
                 <h4 id="add-word">Оқу материалдарын қосу</h4>
                 <div className="card-body">
         <form>
           
           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Оқу материалының аты"></input>
           </div>

           <div className="form-group">
             <textarea name="description" onChange={handleChange} className="form-control" id="InputDescription" placeholder="Оқу материалының сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>

           <div className="form-group">
             <textarea name="remarks" onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Ескертулер"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
         </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
        </>
    );
}
export default AddStudyMaterial;
