import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";
import TeacherSidebar from "./TeacherSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function StudyMaterials(){

    useEffect(()=>{
        document.title='Study materials';
    });

    const [studyData, setstudyData] = useState([]);
    const [totalResult, settotalResult] = useState(0);
    const {course_id} = useParams();
    const [courseData, setCourseData] = useState([]); 
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/study-materials/'+ course_id).then((res)=>{
                setstudyData(res.data);
                settotalResult(res.data.length);
            });
          }catch(error){
            console.log(error);
          }

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const handleDeleteClick = (study_id) => {
        Swal.fire({
            title: 'Confirm',
            text: "Are you sure you want to delete this data?",
            icon: 'info',
            confirmButtonText: 'Continue',
            showCancelButton: true
          }).then((result)=>{
            if(result.isConfirmed){
                try{
                    axios.delete(baseUrl + '/study-material/'+ study_id).then((res)=>{
                        Swal.fire('success', 'Data has been deleted');
                        try{
                            axios.get(baseUrl + '/study-materials/'+ course_id).then((res)=>{
                                settotalResult(res.data.length)
                                setstudyData(res.data);
                            });
                          }catch(error){
                            console.log(error);
                          }
                    });
                  }catch(error){
                    Swal.fire('error', 'Data has not been deleted');
                  }
            }else{
                Swal.fire('error', 'Data has not been deleted');
            }
          });
    }
    return (
        <>

<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Оқу материалдарын қосу</h4>
                 <Link to={"/add-study-material/"+course_id} id="another-button">ҚОСУ</Link>
                 <div class="card-body">

                 <table className="table table-bordered pb-1">
  <tbody>
  {studyData.map((row, index) => 
            <tr key={index}>
              <td>{index + 1}</td>
              <td><span className="card-title">{row.title}</span></td>
              <td><span><Link to={row.upload} id="a">Files</Link></span></td>
              <td><span>{row.remarks}</span></td>
              <td><button id="chat-button" onClick={()=>handleDeleteClick(row.id)}><i class="bi bi-trash3-fill"></i></button></td>
              </tr>
          )}
</tbody>
</table>

    </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
        </>
    );
}
export default StudyMaterials;
