import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function EditChapter(){

    useEffect(()=>{
        document.title='Edit Chapter';
      });

    const [chapterData, setChapterData] = useState({
        course: '',
        title: '',
        description: '',
        video: '',
        remarks: ''
      });
    
    const handleChange=(event)=>{
        setChapterData({
          ...chapterData,
          [event.target.name]: event.target.value
        });
      }

    const handleFileChange=(event)=>{
        setChapterData({
          ...chapterData,
          [event.target.name]: event.target.files[0]
        });
      }

      const {chapter_id}=useParams();

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('course', chapterData.course)
        _formData.append('title', chapterData.title)
        _formData.append('description', chapterData.description)
        _formData.append('video', chapterData.video, chapterData.video.name)
        _formData.append('remarks', chapterData.remarks)
    
        try{
          axios.post(baseUrl+'/chapter/'+chapter_id, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            console.log(res);
            //window.location.href='/teacher-edit-chapter/5';
          });
        }catch(error){
            console.log(error);
            
        }
      }
      //Fetch chapters
      useEffect(()=>{
        try{
            axios.get(baseUrl + '/course-chapters/'+ chapter_id).then((res)=>{
                setChapterData(res.data);
                console.log(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);


   return(
    <>
      <TeacherHeader />
      <h5>Edit Chapters </h5>
        <form>
           
           <div className="form-group">
             <input name="title" value={chapterData.title+""}  onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Бөлімнің атауы" />
           </div>

           <div className="form-group">
             <textarea name="description" value={chapterData.description+""} onChange={handleChange} className="form-control" id="InputDescription" placeholder="Бөлімнің сипаттамасы"></textarea>
           </div>

           <div className="input-group mb-3" >
               <input name="video" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>
           {/* Here should be value for video*/}

           <div className="form-group">
             <textarea name="remarks" value={chapterData.remarks||""} onChange={handleChange} className="form-control" id="InputTechnologies" placeholder="Ескертулер"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
        
    </>
   );
}
export default EditChapter;
