import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";
import QuizResChild from "./QuizResChild";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function AttemptedStudents(){

    useEffect(()=>{
        document.title='Attempted Students';
    });

    const [userData, setuserData] = useState([]);
    const {quiz_id} = useParams();
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/attempted-quiz/'+ quiz_id).then((res)=>{
                setuserData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);
    

    return(
        <>
        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Тест тапсырған студенттер</h4>
        <div class="card-body">

        <table className="table table-bordered pb-1">
  <tbody>
  {userData.map((row, index) => 
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{row.user.full_name}</td>
              <td>{row.user.email}</td>
              <td>
              <button type="button" id="assign-button" data-bs-toggle="modal" data-bs-target={`#resModal${row.id}`}>
                <i class="bi bi-person-check-fill"></i>
              </button>

              <div className="modal fade" id={`resModal${row.id}`} tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <QuizResChild quiz={row.quiz.id} user={row.user.id}/>
              </div>
              </td>
              </tr>
          )}
</tbody>
</table>

</div>
              </div>
            </div> 
          </div>
        </div>
        </div> 


      
        </>
    );
}
export default AttemptedStudents;
