import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function AddAssignment() {

    useEffect(()=>{
        document.title='Add Assignment';
      });

    const [assignmentData, setassignmentData] = useState({
        title: '',
        detail: '',
        upload: '',
      });
    
    const handleChange=(event)=>{
        setassignmentData({
          ...assignmentData,
          [event.target.name]: event.target.value
        });
      }

      const handleFileChange=(event)=>{
        window.URL=window.URL || window.webkitURL;
        var upload = document.createElement('upload');
        upload.src = URL.createObjectURL(event.target.files[0])

        setassignmentData({
          ...assignmentData,
          [event.target.name]: event.target.files[0]
        });
      }


      const {course_id}=useParams();

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('course', course_id)
        _formData.append('title', assignmentData.title)
        _formData.append('detail', assignmentData.detail)
        _formData.append('upload', assignmentData.upload, assignmentData.upload.name)
        
        try{
          axios.post(baseUrl+'/assignment/'+course_id, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            //console.log(res.data);
            //window.location.href='/teacher-add-chapter/5';
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
      <div id="background1">
       <TeacherHeader />
        <div class="container mt-4" >
         <div id="my"><h4>Assignment қосу</h4></div>
         <form>
           
           <div className="form-group">
             <input name="title" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Assignment атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="detail" onChange={handleChange} className="form-control" id="InputDescription" placeholder="Assignment сипаттамасы"></textarea>
           </div>

           <div className="form-group" style={{paddingTop: '21px'}}>
               <input name="upload" onChange={handleFileChange} type="file" className="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>
           
           <div className="form-group">
             <button type="button" onClick={submitForm}>Жүктеу</button>
           </div>

         </form>
        </div>
      </div>
        </>
    );
}
export default AddAssignment;
