import { useState, useEffect  } from "react";
import axios from "axios";
import {useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function UserDetail(){
    let {user_id}=useParams();
    const [userData, setuserData] = useState([])

  useEffect(()=>{

      try{
        axios.get(baseUrl + '/user/' + user_id).then((res)=>{
            setuserData(res.data);
            
        });
      }catch(error){
        console.log(error);
      }

}, []);


    return(
        <>

<div id="back-img">
    <div className="card" id="user-det">
        <div class="container mt-3" >
          <div class="row">
            <div class="col-4">
            <div><img src={userData.profile_img} id="det-img" className="card-img-top" alt="..."></img></div>
            </div>
            <div class="col-6" id="user-info">
                <h4>{userData.full_name}</h4>
                <h6><b>Email</b> {userData.email}</h6>
                <h6><b>UserName:</b> {userData.username}</h6>
                <h6><b>Interested categories:</b> {userData.interested_categories}</h6>
            </div> 
          </div>
        </div>
        </div>
        </div>
          

        </>
    );
}
export default UserDetail;
