}
    return(
        <>
       <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Сүйікті курстарым</h4>
                 <div class="card-body" id="course-body">
      
        <div className="row mb-4">
         {courseData.map((row, index) => 
          <div className="col-md-5 mb-3">
           <div className="card" id="my-courses-cards">
           <img src="https://images.unsplash.com/photo-1503455637927-730bce8583c0?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80" className="card-img-top" height='100' alt="..."></img>
            <div className="card-body">
             <Link id="a" to={'/course_detail/'+ row.course.id}><h5 className="card-title">{row.course.title}</h5></Link>
             <p>{row.course.teacher.full_name}</p>
             <Link id="unenroll" to={'/user-quiz-list/'+ row.course.id} style={{color: 'rgb(191, 57, 79)'}}>Тесттер тізімі</Link><br></br>
             <button id="unenroll" onClick={()=>unenroll(userId)}>Шығу</button>
            </div>
           </div>
         </div>
         )}
        </div>
    
        </div>
        </div>
      </div>
    </div>
  </div>
</div>
        </>
    );
}
export default UserFavoriteCourses;
