import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';


function TeacherProfile() {
  useEffect(()=>{
    document.title='Profile';
});

  const teacherId = localStorage.getItem("teacherId")
  const [teacherData, setTeacherData] = useState(null)

  useEffect(() => {
    try {
        axios.get(baseUrl + '/teacher/' + teacherId + '/').then((res) => {
            setTeacherData(res.data)
        })
    } catch (error) {
        console.log(error)
    }
}, [])

if (!teacherData) {
    return <div>Loading...<Link to="/teacher-logout">Logout</Link></div>
}


    return (
        <>

       
<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">{teacherData.full_name}</h4>
                 <div class="card-body">
    
        <div id="profile-el"><img src={teacherData.profile_img} id="person-img" className="card-img-top" alt="..."></img></div>

        <div id="profile-el">
        <table className="table table-bordered">
  <thead>
    <tr>
      <th scope="col">Full name</th>
      <th scope="col">Email</th>
      <th scope="col">Write</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>{teacherData.email}</td>
      <td>{teacherData.qualification}</td>
      <td>{teacherData.mobile_no}</td>
      <td>{teacherData.skills}</td>
    </tr>
    </tbody>
</table>
        </div>
                </div>
              </div>
            </div> 
          </div>
        </div>
        </div>

        {/** 
        {teacherData.facebook_url &&
         <Link to={teacherData.facebook_url}><i className="bi bi-facebook"></i></Link>
        }
        {teacherData.twitter_url &&
         <Link to={teacherData.twitter_url}><i className="bi bi-twitter"></i></Link>
        } 
        {teacherData.instagram_url &&
         <Link to={teacherData.instagram_url}><i className="bi bi-instagram"></i></Link>
        }
        {teacherData.website_url &&
         <Link to={teacherData.website_url}><i className="bi bi-globe2"></i></Link>
        } 
        */}  
        
        </>
    );
}
export default TeacherProfile;
