import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function UserChangeForgotPassword() {
    useEffect(()=>{
        document.title='Change Forgot Password';
      });

    const [userData, setuserData] = useState({
      'password': ''
    });
    const [successMsg, setsuccessMsg] = useState([]);
    const [errorMsg, seterrorMsg] = useState([]);
    const {user_id}=useParams()

          //Change Element Value
  const handleChange=(event)=>{
    setuserData({
      ...userData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=()=>{
    const userFormData = new FormData();
    userFormData.append("password", userData.password)

    try{
      axios.post(baseUrl + '/user-change-forgot-password/'+user_id+'/', userFormData).then((response)=>{
        if(response.data.bool===true){
            setsuccessMsg(response.data.msg);
            seterrorMsg('');
        }else{
            seterrorMsg(response.data.msg);
            setsuccessMsg('');
        }
      });
    }catch(error){
        console.log(error);
        setuserData({'status': 'error'});
    }  

  };

  const userLoginStatus=localStorage.getItem('userLoginStatus')
  if(userLoginStatus=='true'){
    window.location.href='/user-profile';
  }



    return (
        <>
         {/* Left Part */}
       <div className="leftSide"></div>

<div className="rightSide" id="forgot">
<div id = "register-info">
<h3 id='header-forgot'>Жаңа құпиясөз</h3>
        {successMsg && <p className='text-success'>{successMsg}</p>}
        {errorMsg && <p className='text-danger'>{errorMsg}</p>}
        <div className="form-group">
              <input name="password" type="password" value={userData.password} onChange={handleChange} className="form-control" id="InputPassword1" placeholder="Пошта"></input>
        </div>

        <div className="form-group">
              <button id="login-button" onClick={submitForm} type="submit">өзгерту</button>
        </div>
        </div>
      </div>
        </>
    );
}
export default UserChangeForgotPassword;
