import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";


const baseUrl='http://127.0.0.1:8000/api';

function UserMyTeachers(){
    useEffect(()=>{
        document.title='User My Teachers';
    });
   

    const [teacherData, setteacherData] = useState([]);

    const userId=localStorage.getItem('userId');


   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch-my-teachers/'+ userId).then((res)=>{
            console.log(res.data)
          setteacherData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);

    
    return(
        <>
        <UserHeader />
        <div id="my"><h4>Менің teachers</h4></div> 

        <table className="table">
  <thead>
    <tr>
      <th scope="col">Full name</th>
      <th scope="col">Email</th>
      <th scope="col">Write</th>
    </tr>
  </thead>
  <tbody>
  {teacherData.map((row, index)=> (
            <tr key={index}>
              <td>{row.teacher.full_name}</td>
              <td>{row.teacher.email}</td>
              <td><button className="btn btn-primary" data-bs-toggle="modal" data-bs-target={`#msgModal${index}`}><i className="bi bi-chat-dots-fill"></i></button>

{/**Modal */}

              
              </td>
            </tr>
          ))}
</tbody>
</table>
        </>
    );
}
export default UserMyTeachers;
