import './Home.css';
import CommonLogin from './CommonLogin';
import CommonSignUp from './CommonSignUp';
import {Link} from 'react-router-dom';
import {useEffect, useState} from 'react';

function Home() {
  useEffect(()=>{
    document.title='Home Page';
  });

  const [modalOpen, setModalOpen] = useState(false);
  const [modalSignupOpen, setSignupModalOpen] = useState(false);

  return (
    <>
     
      <div>
        <div id="header-element1">
         <Link to="/"><p className="logo" >lulu school</p></Link>
        </div>

        <div id ="header-element2">
          <ul>
           <li className="nav-item">
            <button id="login-button" onClick={() => {setModalOpen(true); }}>КІРУ</button>
           </li>
           <li className="nav-item">
            <button id="login-button">ҚАЗ</button>
           </li>
          </ul>
        </div>

      </div>


      <div id="body-element">
        <h1>Lulu Chinese Online School</h1>
        <div id="text">
          <p>Қытай тілін 0-ден бастап 3 айда қытайша сөйлейтін деңгейге дейін жеткіземіз. Сонымен қатар бізбен бірге HSK тестіне дайындала аласыз.</p>
        </div>
        <button id="login-button" onClick={() => {setSignupModalOpen(true); }}>Бастау</button>

        {modalOpen && <CommonLogin setOpenModal={setModalOpen} />}
        {modalSignupOpen && <CommonSignUp setSignupOpenModal={setSignupModalOpen} />}
      </div>

      <div id="about-us-section">
        <h4 id="about-us-word">Қытай тілі курсы жайлы</h4>
        <i className="bi bi-grip-horizontal bi-lg"></i>
        <div id="about-us">
          <p id="about-us-text">Қытай тілін 0-ден бастаушыларға арналған курсымыз 3 ай созылады. Осы 1 айдың өзінде сіз қытайша сөйлеуді және жазуды үйренесіз. 1 айдан кейін сіз қарапайым сөйлемдер құрып, өз ойыңызды кытайша жеткізе аласыз. Біздің сабақтар аптасына 2 рет Zoom платформасы арқылы өтеді (таңдаған пакетіңізге байланысты) және видео сабақтарымыз да бар. Әр сабағымыздың ұзақтығы 60-80 минут. Курс аяқталғаннан кейін сіздің деңгейіңіз HSK 2-3 деңгейімен сәйкес келеді. Төлем ақы Kaspi арқылы жүзеге асады.</p>
        </div>
      </div>
    </>
  );
}

export default Home;
