import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyUsers() {

  const msgList={
       height: '500px',
       overFlow: 'auto'
  }

    useEffect(()=>{
        document.title='User List';
    });

    const [studentData, setStudentData] = useState([]);
    const teacherId=localStorage.getItem('teacherId');
    const [msgData, setmsgData] = useState({
      'msg_text': ''
    });
    const [successMsg, setsuccessMsg] = useState([]);
    const [errorMsg, seterrorMsg] = useState([]);
    

   useEffect(()=>{
    try{
      axios.get(baseUrl + '/fetch-all-enrolled-users/'+ teacherId).then((res)=>{
        setStudentData(res.data);
      })
      }catch(error){
        console.log(error);
      }
}, []);
    
if (!studentData) {
  return <div>Loading...</div>
}



const handleChange=(event)=>{
  setmsgData({
    ...msgData,
    [event.target.name]: event.target.value
  });
}

const submitForm=(user_id)=>{
  const msgFormData = new FormData();
  msgFormData.append('msg_text', msgData.msg_text)
  msgFormData.append('msg_from', 'teacher')

  try{
    axios.post(baseUrl + '/send-message/'+teacherId+'/'+user_id, msgFormData).then((response)=>{
      if(response.data.bool===true){
        setmsgData({
          'msg_text': ''
        });
        setsuccessMsg(response.data.msg);
        seterrorMsg('');
    }else{
        seterrorMsg(response.data.msg);
        setsuccessMsg('');
    }
    });
  }catch(error){
    console.log(error);
  }


};


    return (
        <>
        <TeacherHeader />
        <div>{studentData.full_name}</div> 

<table className="table">
  <thead>
    <tr>
      <th scope="col">Full name</th>
      <th scope="col">Email</th>
      <th scope="col">Username</th>
      <th scope="col">Write</th>
    </tr>
  </thead>
  <tbody>
  {studentData.map((row, index)=> (
            <tr key={row.user.id}>
              <td>{row.user.full_name}</td>
              <td>{row.user.email}</td>
              <td>{row.user.username}</td>
              <td><button className="btn btn-primary" data-bs-toggle="modal" data-bs-target={`#exampleModal${index}`}><i className="bi bi-chat-dots-fill"></i></button>

<div className="modal fade" id={`exampleModal${index}`} tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog modal-fullscreen">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="exampleModalLabel">{row.user.full_name}
          <span className="ms-5"><i className="bi bi-bootstrap-reboot"></i></span>
        </h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">

        <div className="row">
          <div className="col-md-8 mb-2 col-12 border-end" style={msgList}>
            {/**From another user */}
            <div className="row">
            <div className="col-5">
              <div className="alert alert-primary" >
                A simple primary alert—check it out!
              </div>
              <small className="text-muted">22-03-2023 10:34</small>
            </div>
            </div>
             {/**From this user */}
             <div className="row">
            <div className="col-5 offset-7">
              <div className="alert alert-warning" >
                A simple primary alert—check it out!
              </div>
              <small className="text-muted">22-03-2023 10:34</small>
            </div>
            </div>
          </div>

         <div className="col-md-4 col-12">
         {successMsg && <p className='text-success'>{successMsg}</p>}
         {errorMsg && <p className='text-danger'>{errorMsg}</p>}
          <form>
           <div class="mb-3">
             <textarea name='msg_text' className="form-control" onChange={handleChange} rows='6' placeholder="Add your message here..."></textarea>
           </div>
           <button type="button"  onClick={()=>submitForm(row.user.id)} class="btn btn-primary">Submit</button>
          </form>
         </div>
        </div>

      </div>
    </div>
  </div>
</div>
              
              </td>
            </tr>
          ))}
</tbody>
</table>
   
        </>
    );
}
export default TeacherMyUsers;
