import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function ContactUs() {

    useEffect(()=>{
        document.title='Add Assignment';
      });
      const [courseData, setCourseData] = useState([]); 
      useEffect(()=>{

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    }, []);


    const [assignmentData, setassignmentData] = useState({
        title: '',
        detail: '',
        upload: '',
      });
    
    const handleChange=(event)=>{
        setassignmentData({
          ...assignmentData,
          [event.target.name]: event.target.value
        });
      }

      const handleFileChange=(event)=>{
        window.URL=window.URL || window.webkitURL;
        var upload = document.createElement('upload');
        upload.src = URL.createObjectURL(event.target.files[0])

        setassignmentData({
          ...assignmentData,
          [event.target.name]: event.target.files[0]
        });
      }


      const {course_id}=useParams();

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('course', course_id)
        _formData.append('title', assignmentData.title)
        _formData.append('detail', assignmentData.detail)
        _formData.append('upload', assignmentData.upload, assignmentData.upload.name)
        
        try{
          axios.post(baseUrl+'/assignment/'+course_id, _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            //console.log(res.data);
            window.location.href='/teacher-add-assignment/'+course_id;
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
         <div id="contact">
      <h4 id="about-us-word">Байланыс</h4>
      <p id="contact-text">Біздің менеджер сізбен қысқа уақыт аралығында хабарласады.</p>
      <form>
           <div className="form-group" id="inp">
             <input name="full_name" type="text" className="form-control" id="InputTitle" placeholder="Aты-жөні"></input>
           </div>

           <div className="form-group" id="inp">
             <input name="email" type="email" className="form-control" id="InputTitle" placeholder="Пошта"></input>
           </div>

           <div className="form-group" id="inp">
             <textarea name="detail" className="form-control" rows='8' id="InputDescription" placeholder="Толығырақ..."></textarea>
           </div>

           <div className="form-group">
             <button type="button" id="login-button">Жүктеу</button>
           </div>

         </form>
      </div>
        </>
    );
}
export default ContactUs;
