import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function MessageList(props){

    useEffect(()=>{
        document.title='Message List';
    });

    const [msgData, setmsgData] = useState([]);

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/get-messages/'+props.teacher_id+'/'+props.user_id).then((res)=>{
                setmsgData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const fetchMsg=()=>{
      try{
        axios.get(baseUrl + '/get-messages/'+props.teacher_id+'/'+props.user_id).then((res)=>{
            setmsgData(res.data);
            const objDiv=document.getElementById("msgList");
            console.log(objDiv);
            objDiv.scrollTop = objDiv.scrollHeight;
        });
      }catch(error){
        console.log(error);
      }
    }
    const msgList={
      height: '500px',
      overflow: 'auto',
 }
    

    return(
        <>
        <span className="ms-5" onClick={fetchMsg} title="Refresh" ><i className="bi bi-bootstrap-reboot"></i></span>
        <div id="msgList" style={msgList}>
         {msgData.map((row, index) =>  
            <div className="row mb-4">
              {row.msg_from == 'teacher' && 
             <div className="col-5 offset-6">
              <div className="alert alert-primary mb-1" >
                {row.msg_text}
              </div>
              <small className="text-muted">{row.msg_time}</small>
             </div>
             }
             {row.msg_from != 'teacher' && 
             <div className="col-5">
              <div className="alert alert-success mb-1" >
                {row.msg_text}
              </div>
              <small className="text-muted">{row.msg_time}</small>
             </div>
             }
            </div>
         )}
         </div>
        </>
    );
}
export default MessageList;
