import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import MessageListUser from "./MessageListUser";


const baseUrl='http://127.0.0.1:8000/api';

function UserMyTeachers(){
    useEffect(()=>{
        document.title='User My Teachers';
    });

    const msgList={
        height: '500px',
        overflow: 'auto'
   }
   

    const [teacherData, setteacherData] = useState([]);

    const userId=localStorage.getItem('userId');


   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch-my-teachers/'+ userId).then((res)=>{
            console.log(res.data)
          setteacherData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);

const [msgData, setmsgData] = useState({
    'msg_text': ''
  });
  const [successMsg, setsuccessMsg] = useState([]);
  const [errorMsg, seterrorMsg] = useState([]);
  const [groupmsgData, setgroupmsgData] = useState({
    'msg_text': ''
  });
  const [groupsuccessMsg, setgroupsuccessMsg] = useState([]);
  const [grouperrorMsg, setgrouperrorMsg] = useState([]);

  const handleChange=(event)=>{
    setmsgData({
      ...msgData,
      [event.target.name]: event.target.value
    });
  }

  const submitForm=(teacher_id)=>{
    const msgFormData = new FormData();
    msgFormData.append('msg_text', msgData.msg_text)
    msgFormData.append('msg_from', 'student')
  
    try{
      axios.post(baseUrl + '/send-message/'+teacher_id+'/'+userId, msgFormData).then((response)=>{
        if(response.data.bool===true){
          setmsgData({
            'msg_text': ''
          });
          setsuccessMsg(response.data.msg);
          seterrorMsg('');
      }else{
          seterrorMsg(response.data.msg);
          setsuccessMsg('');
      }
      });
    }catch(error){
      console.log(error);
    }
  
  
  };
  

    
    return(
        <>
        <UserHeader />
        <div id="my"><h4>Менің teachers</h4></div> 

        <table className="table">
  <thead>
    <tr>
      <th scope="col">Full name</th>
      <th scope="col">Email</th>
      <th scope="col">Write</th>
    </tr>
  </thead>
  <tbody>
  {teacherData.map((row, index)=> (
            <tr key={index}>
              <td>{row.teacher.full_name}</td>
              <td>{row.teacher.email}</td>
              <td><button className="btn btn-primary" data-bs-toggle="modal" data-bs-target={`#smsgModal${index}`}><i className="bi bi-chat-dots-fill"></i></button>

{/**Modal */}
<div className="modal fade" id={`smsgModal${index}`} tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog modal-fullscreen">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="exampleModalLabel">{row.teacher.full_name}
        </h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">

        <div className="row">
          <div className="col-md-8 mb-2 col-12 border-end" style={msgList}>
            <MessageListUser  teacher_id={row.teacher.id} user_id={userId}/>
          </div>

         <div className="col-md-4 col-12">
         {successMsg && <p className='text-success'>{successMsg}</p>}
         {errorMsg && <p className='text-danger'>{errorMsg}</p>}
          <form>
           <div className="mb-3">
             <textarea name='msg_text' value={msgData.msg_text} className="form-control" onChange={handleChange} rows='6' placeholder="Add your message here..."></textarea>
           </div>
           <button type="button"  onClick={()=>submitForm(row.teacher.id)} className="btn btn-primary">Submit</button>
          </form>
         </div>
        </div>

      </div>
    </div>
  </div>
</div>
              
              </td>
            </tr>
          ))}
</tbody>
</table>
        </>
    );
}
export default UserMyTeachers;
