import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function CheckQuizStatusForUser(props){

    const [quizData, setquizData] = useState([]); 

    useEffect(()=>{
        try{
            axios.get(`${baseUrl}/fetch-quiz-attempt-status/${props.quiz}/${props.user}`).then((res)=>{
                setquizData(res.data);
            });
          }catch(error){
            console.log(error);
          }

    }, []);


    return(
        <>

<div>
    
    {quizData.bool === false &&
    <Link to={`/user-take-quiz/${props.quiz}`} id="assign-button"><i className="bi bi-pencil-fill"></i></Link>
   }
   {quizData.bool === true &&
    <span id="a">Тапсырылды</span>
   }
     
</div>   
 


      
        </>
    );
}
export default CheckQuizStatusForUser;
