import { useState } from 'react';
import {Link} from 'react-router-dom';


function UserSidebar() {

    return (
        <>
        <div class="card" id="sidebar">
          <div class="list-group list-group-flush">
            
           <img src="https://1.bp.blogspot.com/-vhmWFWO2r8U/YLjr2A57toI/AAAAAAAACO4/0GBonlEZPmAiQW4uvkCTm5LvlJVd_-l_wCNcBGAsYHQ/s16000/team-1-2.jpg" alt="..." id="profile-img"/>
           <h5 id="sidebar-word">Anamika Roy</h5>
           <h5 id="sidebar-text">Студент</h5>
            
           <Link to="/user-my-courses" id="sidebar-el" class="list-group-item list-group-item-action"><i class="bi bi-book-half" id="sidebar-it" ></i>Менің курстарым</Link>
           <Link to="/user-favorite-courses" id="sidebar-el" class="list-group-item list-group-item-action"><i class="bi bi-heart-fill" id="sidebar-it"></i>Сүйікті курстарым</Link>
           <Link to="/available-courses" id="sidebar-el" class="list-group-item list-group-item-action"><i class="bi bi-people-fill" id="sidebar-it"></i>Ұсынылған курстар</Link>
           <Link to="/user-my-teachers" id="sidebar-el" class="list-group-item list-group-item-action"><i class="bi bi-file-earmark-text-fill" id="sidebar-it"></i>Мұғалімдерім</Link>
           <Link to="/user-profile-settings" id="sidebar-el" class="list-group-item list-group-item-action"><i class="bi bi-gear-fill" id="sidebar-it"></i>Баптаулар</Link>
           <Link to="/user-change-password" id="sidebar-el" class="list-group-item list-group-item-action"><i class="bi bi-person-fill-lock" id="sidebar-it"></i>Құпия сөзді өзгерту</Link>
           <Link to="/user-logout" id="sidebar-el" class="list-group-item list-group-item-action"><i class="bi bi-x-circle-fill" id="sidebar-it"></i>Шығу</Link>
          </div>
        </div>

        </>
    );
}
export default UserSidebar;
