import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import TeacherSidebar from "./TeacherSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function AllChapters(){

    useEffect(()=>{
        document.title='All Chapters';
    });

    const [chapterData, setChapterData] = useState([]);
    const [courseData, setCourseData] = useState([]); 
    const [totalResult, settotalResult] = useState(0);
    const {course_id} = useParams();
    const [assignmentData, setassignmentData] = useState([])
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/course-chapters/'+ course_id).then((res)=>{
                setChapterData(res.data);
                settotalResult(res.data.length);
            });
          }catch(error){
            console.log(error);
          }

          try{
            axios.get(baseUrl + '/course/' + course_id).then((res)=>{
                setCourseData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const Swal = require('sweetalert2');
    const handleDeleteClick=()=>{
        Swal.fire({
            title: 'Растау',
            text: 'Сабақты өшіргіңіз келе ме?',
            icon: 'info',
            confirmButtonText: 'Cool',
            showCancelButton: true
          })
    }

    useEffect(()=>{
      try{
          axios.get(baseUrl + '/assignment/'+ course_id).then((res)=>{
              setassignmentData(res.data);
              //settotalResult(res.data.length);
          });
        }catch(error){
          console.log(error);
        }
  }, []);

  const handleDeleteAssignment = (assignment_id) => {
    Swal.fire({
        title: 'Confirm',
        text: "Are you sure you want to delete this data?",
        icon: 'info',
        confirmButtonText: 'Continue',
        showCancelButton: true
      }).then((result)=>{
        if(result.isConfirmed){
            try{
                axios.delete(baseUrl + '/assignmentd/'+ assignment_id).then((res)=>{
                    Swal.fire('success', 'Data has been deleted');
                    try{
                        axios.get(baseUrl + '/assignment/'+ course_id).then((res)=>{
                            settotalResult(res.data.length)
                            setassignmentData(res.data);
                        });
                      }catch(error){
                        console.log(error);
                      }
                });
              }catch(error){
                Swal.fire('error', 'Data has not been deleted');
              }
        }else{
            Swal.fire('error', 'Data has not been deleted');
        }
      });
}

const handleDeleteChapter = (chapter_id) => {
  Swal.fire({
      title: 'Confirm',
      text: "Are you sure you want to delete this data?",
      icon: 'info',
      confirmButtonText: 'Continue',
      showCancelButton: true
    }).then((result)=>{
      if(result.isConfirmed){
          try{
              axios.delete(baseUrl + '/chapter/'+ chapter_id).then((res)=>{
                  Swal.fire('success', 'Data has been deleted');
                  try{
                      axios.get(baseUrl + '/chapters/'+ course_id).then((res)=>{
                          settotalResult(res.data.length)
                          setChapterData(res.data);
                      });
                    }catch(error){
                      console.log(error);
                    }
              });
            }catch(error){
              Swal.fire('error', 'Data has not been deleted');
            }
      }else{
          Swal.fire('error', 'Data has not been deleted');
      }
    });
}

const handleDeleteCourse = (course_id) => {
  Swal.fire({
      title: 'Confirm',
      text: "Are you sure you want to delete this data?",
      icon: 'info',
      confirmButtonText: 'Continue',
      showCancelButton: true
    }).then((result)=>{
      if(result.isConfirmed){
          try{
              axios.delete(baseUrl + '/course/'+ course_id).then((res)=>{
                  Swal.fire('success', 'Data has been deleted');
                  try{
                      axios.get(baseUrl + '/course/').then((res)=>{
                          settotalResult(res.data.length)
                          setCourseData(res.data);
                          window.location.href='/teacher-my-courses';
                      });
                    }catch(error){
                      console.log(error);
                    }
              });
            }catch(error){
              Swal.fire('error', 'Data has not been deleted');
            }
      }else{
          Swal.fire('error', 'Data has not been deleted');
      }
    });
}
    return (
        <>

<div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">{courseData.title}</h4>
                 <div class="card-body">
                  
      <Link className="btn btn-info" to={"/assign-quiz/"+course_id}>Assign quiz</Link>
      <Link className="btn btn-warning" to={"/teacher-study-materials/"+course_id}>Study Materials</Link>
      <Link className="btn btn-primary" to={"/teacher-add-chapter/"+course_id}>AddChapter</Link>
      <Link className="btn btn-success" to={"/teacher-edit-course/"+course_id}>Edit Course</Link>
      <Link to={"/teacher-add-assignment/"+course_id} className="btn btn-dark">Add Assignment</Link>
      <button className="btn btn-danger" onClick={()=>handleDeleteCourse(course_id)}>Delete</button>

      <div className="container mt-5" >
        <div id="my"><h4>Барлық сабақтар ({totalResult})</h4></div>    
        <div className="row mb-4">
        {chapterData.map((chapter, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title">{chapter.title}</h5>
             
                <Link to={chapter.upload}>Files</Link>

               <h5>{chapter.remarks}</h5>
               <button  onClick={()=>handleDeleteChapter(chapter.id)} className="btn btn-danger"><i className="bi bi-trash3-fill"></i></button>
               <Link to={'/teacher-edit-chapter/'+chapter.id}  className="btn btn-success"><i className="bi bi-pencil-square"></i></Link>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>

     <div className="container mt-5" >
        <div id="my"><h4>All assignments</h4></div>    
        <div className="row mb-4">
        {assignmentData.map((row, index) => 
          <div className="col-md-4 mb-3">
           <div className="card" id="my-courses-cards">
            <div className="card-body">
             <h5 className="card-title">{row.title}</h5>
             
               <Link to={row.upload}>Files</Link>

               <h5>{row.detail}</h5>
               <button className="btn btn-danger" onClick={() => handleDeleteAssignment(row.id)}><i className="bi bi-trash3-fill"></i></button>
               <Link to={'/teacher-edit-assignment/'+row.id}  className="btn btn-success"><i className="bi bi-pencil-square"></i></Link>
            </div>
           </div>
         </div>
         )}
        </div>
     </div>  

     </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
        </>
    );
}
export default AllChapters;
