import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";

const baseUrl='http://127.0.0.1:8000/api';

function TeacherAddCourse() {

    const [cats, setCats]=useState([]);

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/category').then((res)=>{
              
                setCats(res.data);
              
            });
          }catch(error){
            console.log(error);
          }
    }, []);
    console.log(cats);

    return (
        <>
        <TeacherHeader />
        
        <form>
           <div className="form-group">
             <select name="category"  type="text" className="form-control" id="InputCategory" >
              <option selected>Курстың категориясын таңдаңыз</option>
             </select>
           </div>

           <div className="form-group">
             <input name="title"  type="text" className="form-control" id="InputTitle" placeholder="Курстың атауы"></input>
           </div>

           <div className="form-group">
             <textarea name="description"  type="text" className="form-control" id="InputDescription" placeholder="Курстың сипаттамасы"></textarea>
           </div>

           <div class="input-group mb-3" >
               <input name="uploadFile" type="file" class="form-control" id="inputGroupFile03" aria-label="Upload" ></input>
           </div>

           <div className="form-group">
             <textarea name="technologies"  type="text" className="form-control" id="InputTechnologies" placeholder="Технологиялар"></textarea>
           </div>
           
           <div className="form-group">
             <button type="submit">Жүктеу</button>
           </div>

         </form>

        </>
    );
}
export default TeacherAddCourse;
