import UserHeader from "./UserHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import Swal from "sweetalert2";
import UserSidebar from "./UserSidebar";


const baseUrl='http://127.0.0.1:8000/api';

function UserMyCourses(){
    useEffect(()=>{
        document.title='User My Courses';
    });
   
    const[enrollStatus, setEnrollStatus]=useState();
    const [courseData, setCourseData] = useState([]);
    let {course_id}=useParams();
    const userId=localStorage.getItem('userId');
    const [searchString, setSearchString] = useState({
      'search': ''
    });

    //Change Element Value
const handleChange=(event)=>{
  console.log(searchString)
  setSearchString({
    ...searchString,
    [event.target.name]: event.target.value
    
  });
}



   useEffect(()=>{
    try{
        axios.get(baseUrl + '/fetch_enrolled_courses/'+ userId).then((res)=>{
          setCourseData(res.data);
        });
      }catch(error){
        console.log(error);
      }
}, []);

const unenrollCourse=() => {
 
}



    
    const searchCourse = () =>{
      window.location.href='/user-search/'+ searchString.search
    }
    
    return(
        <>
        
        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <UserSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">Менің курстарым</h4>

        <nav className="navbar navbar-light" id="search-section">
         <div className="container-fluid">
          <form className="d-flex">
           <input name="search" id="search-input" onChange={handleChange} className="form-control me-2" type="search" placeholder="Іздеу..." aria-label="Search"/>
           <button onClick={searchCourse} id="search-button" type="button"><i class="bi bi-search"></i></button>
          </form>
         </div>
        </nav>

        <div class="card-body" id="course-body" > 
        <div className="row mb-4">
         {courseData.map((row, index) => 
          <div className="col-md-5 mb-3">
           <div className="card" id="my-courses-cards">
           <img src="https://images.unsplash.com/photo-1503455637927-730bce8583c0?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1470&q=80" className="card-img-top" height='100'  alt="..."></img>
            <div className="card-body">
             <Link id="a" to={'/course_detail/'+ row.course.id}><h5 className="card-title">{row.course.title}</h5></Link>
             <p>{row.course.teacher.full_name}</p>
             <Link id="unenroll" to={'/user-quiz-list/'+ row.course.id} style={{color: 'rgb(191, 57, 79)'}}>Тесттер тізімі</Link><br></br>
             <button id="unenroll" onClick={unenrollCourse}>Шығу</button>
            </div>
           </div>
         </div>
         )}
       </div>
     </div>
        </div>
      </div>
    </div>
  </div>
</div>  
 
        </>
    );
}
export default UserMyCourses;
