import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link, useParams } from "react-router-dom";
import './TeacherMyCourses.css';
import Swal from "sweetalert2";

const baseUrl='http://127.0.0.1:8000/api';

function QuizResChild(props){

    
    const [resultData, setResultData] = useState([]);
    //const {quiz_id} = useParams();

    useEffect(()=>{
        try{
            axios.get(`${baseUrl}/fetch-quiz-result/${props.quiz}/${props.user}`).then((res)=>{
                setResultData(res.data);
            });
          }catch(error){
            console.log(error);
          }

    }, []);

    return(
        <>
          <div className="modal-dialog">
    <div className="modal-content">
      <div className="modal-header">
        <h1 className="modal-title fs-5" id="exampleModalLabel">Тест нәтижесі</h1>
        <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      </div>
      <div className="modal-body">

      <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Жалпы сұрақтар саны</th>
                        <td >{resultData.total_questions}</td>
                    </tr>
                    <tr>
                        <th scope="col">Тапсырылған сұрақтар саны</th>
                        <td>{resultData.total_attempted_questions}</td>
                    </tr>
                    <tr>
                        <th scope="col">Дұрыс жауаптар саны</th>
                        <td id="a">{resultData.total_correct_questions}</td>
                    </tr>
                </thead>
            </table>
    

      </div>
     
    </div>
  </div>


      
        </>
    );
}
export default QuizResChild;
