import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const baseUrl='http://127.0.0.1:8000/api';

function ContactUs() {

    useEffect(()=>{
        document.title='Contact Us';
      });
      const [contactData, setcontactData] = useState({
        'full_name':'',
        'email':'',
        'detail':'',
        'status':''
      }); 
    
    const handleChange=(event)=>{
        setcontactData({
          ...contactData,
          [event.target.name]: event.target.value
        });
      }

      const submitForm=()=>{
        const _formData = new FormData();
        _formData.append('full_name', contactData.full_name)
        _formData.append('email', contactData.email)
        _formData.append('detail', contactData.detail)
        
        try{
          axios.post(baseUrl+'/contact/', _formData).then((res)=>{
            setcontactData({
                'full_name':'',
                'email':'',
                'detail':'',
                'status':'success'
            });
          });
        }catch(error){
            console.log(error);
            setcontactData({'status':'error'})
        }
    
        
    
      };

    return (
        <>
         <div id="contact">
      <h4 id="about-us-word">Байланыс</h4>
            {contactData.status=='success' && <p className="text-success">Байланысқа шыққаныңыз үшін рахмет!</p>}
            {contactData.status=='error' && <p className="text-danger">Қате!</p>}
      <p id="contact-text">Біздің менеджер сізбен қысқа уақыт аралығында хабарласады.</p>
      <form>
           <div className="form-group" id="inp">
             <input name="full_name" onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Aты-жөні"></input>
           </div>

           <div className="form-group" id="inp">
             <input name="email" onChange={handleChange} type="email" className="form-control" id="InputTitle" placeholder="Пошта"></input>
           </div>

           <div className="form-group" id="inp">
             <textarea name="detail" onChange={handleChange} className="form-control" rows='8' id="InputDescription" placeholder="Толығырақ..."></textarea>
           </div>

           <div className="form-group">
             <button type="button" onClick={submitForm} id="login-button">Жүктеу</button>
           </div>

         </form>
      </div>
        </>
    );
}
export default ContactUs;
