import TeacherHeader from "./TeacherHeader";
import { useState, useEffect  } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import './TeacherMyCourses.css';

const baseUrl='http://127.0.0.1:8000/api';

function TeacherMyCourses() {

    useEffect(()=>{
        document.title='My Courses';
    });

    const [courseData, setCourseData] = useState([]);

    const teacherId=localStorage.getItem('teacherId');
    

    useEffect(()=>{
        try{
            axios.get(baseUrl + '/teacher-courses/'+ teacherId).then((res)=>{
                setCourseData(res.data);
            });
          }catch(error){
            console.log(error);
          }
    }, []);

    const styling={
        
        width: "20rem"
     }


    return (
        <>
        
        <TeacherHeader />

        <div class="card-group">
  <div class="card">
    <img src="..." class="card-img-top" alt="..."/>
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
      <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
    </div>
  </div>
  <div class="card">
    <img src="..." class="card-img-top" alt="..."/>
    <div class="card-body">
      <h5 class="card-title">Card title</h5>
      <p class="card-text">This card has supporting text below as a natural lead-in to additional content.</p>
      <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
    </div>
  </div>
 
</div>

        <div class="card text-center" style={styling}>
        {courseData.map((course, index) => 
			<div class="card-body">
                <img src={course.featured_img} className="card-img-top" alt="..."></img>
				<h5 class="card-title"><Link to={'/teacher-all-chapters/'+ course.id}>{course.title}</Link></h5>
				<p class="card-text">Total enrolled: 123</p>
				<button>Delete</button>
                <Link to={'/teacher-add-chapter/'+ course.id}><button >Add Chapter</button></Link>
                <Link to={'/teacher-edit-course/'+ course.id}><button >Edit</button></Link>
			</div>
        )}
		</div>
       
        </>
    );
}
export default TeacherMyCourses;
