import { useState, useEffect  } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import Swal from "sweetalert2";
import TeacherSidebar from "./TeacherSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function EditQuiz() {

    useEffect(()=>{
        document.title='Тестті өзгерту';
      });

      const teacherId=localStorage.getItem('teacherId');

    const [quizData, setquizData] = useState({
        title: '',
        detail: ''
      });

    const {quiz_id}=useParams();  
    useEffect(()=>{
          try{
            axios.get(baseUrl + '/teacher-quiz-detail/'+quiz_id+'/').then((res)=>{
                setquizData({
                    title: res.data.title,
                    detail: res.data.detail
                });
            });
          }catch(error){
            console.log(error);
          }
         

          try{
            axios.get(baseUrl + '/quiz/' + quiz_id).then((res)=>{
                setquizData(res.data);
                
            });
          }catch(error){
            console.log(error);
          }

      
    }, []);
    
    const handleChange=(event)=>{
        setquizData({
          ...quizData,
          [event.target.name]: event.target.value
        });
      }

      
      const submitForm=()=>{
        
        const _formData = new FormData();
        _formData.append("teacher", teacherId)
        _formData.append("title", quizData.title)
        _formData.append("detail", quizData.detail)

        try{
            axios.put(baseUrl+'/teacher-quiz-detail/'+quiz_id+'/', _formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
          }).then((res)=>{
            if(res.status == 200){
                Swal.fire({
                  title: 'updated',
                  icon: 'success',
                  toast: true,
                  timer: 3000,
                  position: 'top-right',
                  timerProgressBar: true,
                  showConfirmButton: false
                });
            }
          });
        }catch(error){
            console.log(error);
            
        }
    
        
    
      };

    return (
        <>
        <div id="back-img">
        <div class="container mt-4" >
          <div class="row">
            <aside class="col md-3">
              <TeacherSidebar/>
            </aside>
            <div class="col-9">
              <div class="card">
                 <h4 id="add-word">{quizData.title} өзгерту</h4>
                 <div class="card-body">
        <form>
        <div className="form-group">
             <input name="title" value={quizData.title} onChange={handleChange} type="text" className="form-control" id="InputTitle" placeholder="Aтауы"></input>
           </div>

           <div className="form-group">
             <textarea name="detail" value={quizData.detail} onChange={handleChange} className="form-control" id="InputDescription" placeholder="Detail"></textarea>
           </div>
           
           <div className="form-group">
             <button type="button" id="login-button" onClick={submitForm}>Өзгерту</button>
           </div>

         </form>
         </div>
              </div>
            </div> 
          </div>
        </div>
        </div> 
        </>
    );
}
export default EditQuiz;
