import axios from "axios";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import UserSidebar from "./UserSidebar";

const baseUrl='http://127.0.0.1:8000/api';

function UserProfile(){
    const [studentData, setStudentData] = useState([]);
    const [imgSrc, setImgSrc] = useState("https://icon-library.com/images/x-button-icon/x-button-icon-20.jpg");
    const userId=localStorage.getItem('userId');
    
    useEffect(() => {
        document.title='Profile';
        try {
            axios.get(baseUrl + '/user/' + userId + '/').then((res) => {
                setStudentData(res.data)
            })
        } catch (error) {
            console.log(error)
        }
    }, [])

    const handleImageError=(event)=> {
        console.log('Image failed to load');
        event.target.src = "https://icon-library.com/images/x-button-icon/x-button-icon-20.jpg";
    }

    if (!studentData) {
        return <div>Loading...</div>
    }


    return(
        
        <>
        <div id="back-img">
        <div className="container mt-4" >
          <div className="row">
            <aside className="col md-3">
              <UserSidebar/>
            </aside>
            <div className="col-9">
              <div className="card">
                 <h4 id="add-word">{studentData.full_name}</h4>
                 <div className="card-body" id="profile-body">
    
        <div id="profile-el2"><img src='https://static7.depositphotos.com/1003835/747/v/950/depositphotos_7470044-stock-illustration-chinese-dragon.jpg'  id="user-img2" className="card-img-top" alt="Изображение студента"></img></div>

        <div id="profile-el2">
    
<table id="profile-table" className="table table-bordered">
                <thead>
                    <tr>
                        <th scope="col">Пошта</th>
                        <td>{studentData.email}</td>
                    </tr>
                    <tr>
                        <th scope="col">Қолданушы аты</th>
                        <td>{studentData.username}</td>
                    </tr>
                    <tr>
                        <th scope="col">Қызығушылығы</th>
                        <td>{studentData.interested_categories}</td>
                    </tr>
                </thead>
            </table>
    
        </div>
                </div>
              </div>
            </div> 
          </div>
        </div>
        </div>
</>
    );
}
export default UserProfile;
